package loops;

public class ForLoopUse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		for(int i=9; i<=90; i=i+9)
		{
			System.out.println(i);
			
		}
		System.out.println("=========");
		
		for(int i=90; i>=9; i=i-9)
			
		{
			System.out.println(i);
		}
	}

}
